mapper.update:
update member 
set user_pwd=#{encPwd}
where email=#{myEmail} and enroll_type='W'

update member
set user_status = 'N'
where email = #{userEmail}

update member set user_name = #{userName}, nickname = #{nickName}, phone = #{phone}, address = #{address},
origin_name = #{originName},
change_name = #{changeName}, modify_date = sysdate where email = #{email}

update member
set user_pwd = #{userPwd}
where email = #{email}

mapper.select:
select * 
from report
where report_yn = 'Y' and target_user = #{email}

select count(*)
from g_order
where email = #{email}

select *
from g_order join goods using (goods_id)
where email = #{email}
order by order_date desc
